        background-color: var(--center-channel-bg);
        grid-area: header;

        .insights-title {
            display: flex;
            align-items: center;
            padding: 2px 6px 2px 8px;
            border: none;
            margin: 0;
            background-color: transparent;
            border-radius: 4px;
            color: rgba(var(--center-channel-color-rgb), 1);
