            &:hover {
                background-color: rgba(var(--center-channel-color-rgb), 0.08);
                cursor: pointer;
            }
        }

        .MenuWrapper--open {
            button.insights-title {
                background-color: rgba(var(--button-bg-rgb), 0.08);
            }
        }

        .MenuItem {
            i.icon {
                width: 16px;
                height: 16px;
                color: rgba(var(--center-channel-color-rgb), 0.56);

                &::before {
                    margin: 0;
