.PostAttachmentOpenGraph.original:
.PostAttachmentOpenGraph {
    position: relative;
    display: grid;
    width: 100%;
    max-width: 700px;
    padding: 16px;
    border: 1px solid rgba(var(--center-channel-color-rgb), 0.16);
    margin: 5px 0;
    background-color: var(--center-channel-bg);
    border-radius: 4px;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.08);

    /**
     * This is a little hacky way to say every column that gets auto-generated
     * by placing an item outside of the grid will get 80px wide. We use this here
     * to place a small image outside of the grid, but a big one in the row below
     */
    grid-auto-columns: 80px;
    grid-template-columns: 1fr;

    /** For some reason just setting this on :hover is not sufficient, therefore the !important */
    text-decoration: none !important;
    transition: box-shadow;

    &:hover:not(:active) {
        border-color: rgba(var(--center-channel-color-rgb), 0.24);
        box-shadow: 0 4px 6px rgba(0, 0, 0, 0.12);
    }

    &:hover {
        .remove-button {
            display: block;
        }
    }

    &:active {
        border-color: rgba(var(--button-bg-rgb), 0.32);
    }

    .remove-button {
        position: absolute;
        z-index: 10;
        top: -1px;
        left: -28px;
        display: none;
        padding: 5px;
        background-color: rgba(var(--center-channel-color-rgb), 0);
        border-radius: 4px;
        color: rgba(var(--center-channel-color-rgb), 0.56);

        &:hover:not(:active) {
            background-color: rgba(var(--center-channel-color-rgb), 0.08);
            color: rgba(var(--center-channel-color-rgb), 0.56);
        }

        &:active {
            background-color: rgba(var(--button-bg-rgb), 0.16);
            color: rgba(var(--button-bg-rgb), 1);
        }
    }

    &__body {
        .sitename,
        .title {
            @extend .truncate;

            -webkit-line-clamp: 1;
            word-break: break-all;
        }

        .sitename {
            color: rgba(var(--center-channel-color-rgb), 0.64);
            font-size: 11px;
            font-weight: 400;
            line-height: 16px;
        }

        .title {
            margin-bottom: 4px;
            color: var(--link-color);
            font-size: 14px;
            font-weight: 600;
            line-height: 20px;
        }

        .description {
            @extend .truncate;

            color: var(--center-channel-color);
            font-size: 14px;
            font-weight: 400;
            -webkit-line-clamp: 2;
            line-height: 20px;
            word-break: none;
        }

        .isInPermalink {
            overflow: hidden;
            width: 100%;
            height: 1.5em;
            line-height: 1.5em;
            text-overflow: ellipsis;
            white-space: nowrap;
        }
    }

    &__image {
        position: relative;
        display: flex;
        overflow: visible;
        height: 80px;
        border-radius: 4px;
        grid-column: 2 / 3;
        grid-row: 1 / 2;
        place-content: stretch;
        place-items: stretch;

        &.large {
            width: auto;
            max-width: 392px;
            height: auto;
            max-height: 240px;
            margin-top: 12px;
            grid-column: 1 / 2;
            grid-row: 2 / 3;
            place-content: center;
            place-items: center;
        }

        figure {
            overflow: hidden;
            max-width: 392px;
            max-height: 240px;
            border-radius: 4px;

            img {
                min-width: 100%;
                min-height: 100%;
                object-fit: cover;
            }
        }

        .preview-toggle {
            position: absolute;
            top: -4px;
            left: -4px;
            display: flex;
            width: 24px;
            height: 24px;
            border: 1px solid rgba(var(--center-channel-color-rgb), 0.16);
            background-color: var(--center-channel-bg);
            border-radius: 50%;
            color: rgba(var(--center-channel-color-rgb), 0.56);
            font-size: 12px;
            place-content: center;
            place-items: center;
        }

        &.collapsed {
            place-content: start;
            place-items: start;

            .preview-toggle {
                position: relative;
                top: unset;
                left: unset;
                display: flex;
                width: auto;
                border: none;
                background-color: transparent;
                border-radius: 0;
            }
        }

        &:hover:not(.collapsed) {
            .preview-toggle {
                display: flex;
            }
        }
    }
}

.Reaction.original:
.Reaction {
    @include button-style--none;

    display: inline-flex;
    min-width: 28px;
    height: 24px;
    align-items: center;
    padding: 0 5px;
    border: 1px solid transparent;
    margin: 0 4px 4px 0;
    background: rgba(var(--center-channel-color-rgb), 0.08);
    border-radius: 4px;
    color: rgba(var(--center-channel-color-rgb), 0.64);
    cursor: pointer;
    fill: rgba(var(--center-channel-color-rgb), 0.56);
    font-size: 11px;
    font-weight: 600;
    transition:
        border-color 100ms linear,
        background-color 100ms linear;
    user-select: none;

    &:hover {
        border: solid 1px rgba(#3d3c40, 0.16);
        background-color: transparent;
        color: rgba(#3d3c40, 0.72);
    }

    &:not(.Reaction--reacted) {
        &:hover {
            border-color: rgba(var(--center-channel-color-rgb), 0.16);
            background-color: v(center-channel-bg);
            color: rgba(var(--center-channel-color-rgb), 0.72);
            fill: rgba(var(--center-channel-color-rgb), 0.72);
        }

        &:active {
            background-color: rgba(var(--button-bg-rgb), 0.08);
            color: v(button-bg);
            fill: v(button-bg);
        }
    }

    &.Reaction--read-only {
        cursor: default;
    }

    &__add {
        position: relative;
        font-size: 20px;
        line-height: 0;
        vertical-align: middle;
    }

    &__emoji {
        min-width: 16px;
        max-width: 16px;
        min-height: 16px;
        max-height: 16px;
        margin: 0 2px 0 0;
        object-fit: contain;
        vertical-align: middle;
    }

    &__emoji--post-menu {
        width: 18px;
        height: 18px;
        vertical-align: middle;
    }

    &__count {
        display: flex;
        overflow: hidden;
        flex-direction: row-reverse;
        justify-content: flex-start;
        line-height: 16px;
        pointer-events: none;
    }

    &.Reaction--reacted,
    &.Reaction--reacting {
        border-color: v(button-bg);
        background-color: rgba(var(--button-bg-rgb), 0.08);
        color: v(button-bg);

        &:hover {
            border-color: v(button-bg);
            background: v(center-channel-bg);
        }

        &:active {
            background-color: rgba(var(--button-bg-rgb), 0.08);
            color: v(button-bg);
            fill: v(button-bg);
        }
    }

    &.Reaction--reacting {
        transition: border-color 200ms ease-in-out;

        .Reaction__number--display {
            visibility: hidden;
        }

        .Reaction__number--reacted,
        .Reaction__number--unreacted {
            display: inline-block;
            animation-duration: 200ms;
            animation-fill-mode: forwards;
            animation-name: reacted-anim;
            animation-timing-function: ease-in-out;
        }
    }

    &.Reaction--unreacting {
        transition: border-color 200ms ease-in-out;

        .Reaction__number--display {
            visibility: hidden;
        }

        .Reaction__number--reacted,
        .Reaction__number--unreacted {
            display: inline-block;
            animation-direction: reverse;
            animation-duration: 200ms;
            animation-fill-mode: forwards;
            animation-name: reacted-anim;
            animation-timing-function: ease-in-out;
        }
    }

    .Reaction__number {
        position: relative;

        &--display {
            display: inline-block;
        }

        &--reacted {
            position: absolute;
            top: 100%;
            left: 0;
            display: none;
        }

        &--unreacted {
            position: absolute;
            top: 0%;
            left: 0;
            display: none;
        }
