@import "../styles/mixins";

.movie__details {
  position: relative;
  width: 90%;
  margin: 0 auto;
  padding-top: 10rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
  color: white;
  opacity: 0;
  animation: opacity 1s ease forwards;
  @include respond(tab-port) {
    padding-top: 12rem;
    width: 95%;
  }
  @include respond(phone) {
    padding-top: 10rem;
  }
}
@keyframes opacity {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

.detail__content {
  display: grid;
  grid-template-columns: 4fr 1fr;
  grid-template-rows: auto auto;
  justify-content: center;

  @include respond(tab-port) {
    display: flex;
    flex-direction: column-reverse;
    align-items: center;
  }

  &__poster {
    position: relative;
    @include respond(phone) {
      width: 100%;
    }

    &__overlay {
      position: absolute;
      z-index: 2;
      top: 0;
      right: 0;
      width: 100%;
      height: 100%;

      @include respond(tab-port) {
        background: linear-gradient(
          0deg,
          rgba(17, 17, 17, 0.758) 40%,
          transparent 60%
        );
      }
    }
  }

  &__poster > img {
    max-height: 50rem;
    object-fit: contain;
    border-radius: 1rem;

    @include respond(tab-port) {
      width: 100%;
    }
    @include respond(phone) {
      object-fit: cover;
      width: 100%;
    }
  }
}

.detail {
  &__info {
    display: flex;
    flex-direction: column;
    gap: 2rem;
    padding: 4rem 2rem;
    // display: none;
    opacity: 0;
    visibility: hidden;

    @include respond(tab-port) {
      margin-top: -15rem;
      margin-top: -15rem;
      position: relative;
      z-index: 3;
    }
    @include respond(phone) {
      margin-top: -12.5rem;
    }
  }
  &__title {
    font-size: 5rem;
    font-size: 600;

    &::first-letter {
      color: #e50914;
    }

    @include respond(phone) {
      font-size: 3rem;
    }
  }

  &__additional__info {
    display: flex;
    flex-wrap: wrap;
    gap: 1.5rem;
    font-size: 1.8rem;
    opacity: 0.9;
    font-weight: 400;

    @include respond(phone) {
      font-size: 1.5rem;
    }
  }
  &__rating {
    color: #46d369;
    opacity: 1;
  }
  &__genre > span {
    margin-left: 2rem;
    position: relative;
    &::after {
      content: "&";
      position: absolute;
      font-size: 1.5rem;
      bottom: 0;
      margin-left: 0.5rem;
    }
    &:first-child {
      margin-left: 0;
    }
    &:last-child::after {
      content: "";
    }
  }

  &__overview {
    max-width: 70%;
    font-size: 1.8rem;
    font-weight: 500;
    word-spacing: 0.2rem;

    @include respond(phone) {
      font-size: 1.5rem;
      max-width: 90%;
    }
  }
  &__externalLinks {
    display: flex;
    gap: 2rem;
  }
  &__externalLink {
    text-decoration: none;
    color: rgb(255, 255, 255);
    transition: color 0.2s ease 0s;
    &__icon {
      width: 2rem;
      stroke: white;
    }
    &:hover {
      color: #e50914;
    }
  }

  &__cast {
    opacity: 0;
    transition: all 1s;

    &__title {
      font-size: 3rem;
      font-weight: 500;
    }

    &__members {
      margin: 2rem 0;
      display: flex;
      gap: 2rem;

      @include respond(phone) {
        flex-wrap: wrap;
        align-items: flex-start;
      }
    }
  }
}
.cast {
  max-width: 100%;
  &__member {
    text-align: center;
  }
  &__image {
    height: 8rem;
    width: 8rem;
    border-radius: 50%;
    object-fit: cover;
    cursor: pointer;
    opacity: 0.8;
    transition: all 0.3s;
    &:hover {
      opacity: 1;
    }
    @include respond(phone) {
      height: 5rem;
      width: 5rem;
    }
  }
  &__name {
    font-size: 1.5rem;
    width: 12rem;
    @include respond(phone) {
      width: 10rem;
    }
  }
}

.error__container {
  color: white;
  height: 90vh;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;

  & h1 {
    font-size: 5rem;
    @include respond(phone) {
      font-size: 2.5rem;
    }
    & span {
      color: #e50914;
    }
  }
}

