.engagementEntity.original:
.engagementEntity {
  max-width: $engagement-entity-max-width;
  @include media('>md') {
    @include spr-rule(border-radius, borderRadius-6);
  }
  .engagementEntityContent {
    @include media('>md') {
      @include spr-rule(padding, padding-4);
      .parentMsg {
        left: 0;
        .openDetailPaneActionIcn {
          @include size(1.4rem);
          .icon {
            fill: $charcoal;
          }
        }
      }
    }
  }

  .dstMsgHeader {
    a:hover {
      text-decoration: underline;
    }

    .msgTitle--highlight {
      color: $active-color;
    }
  }

  .msgContentSection,
  .engagementEntitySection {
    clear: both;
    overflow: hidden;
  }

  .msgHeaderSubText {
    display: block;
    float: left;
    margin-right: 0.5 * $spacer;
    @include spr-rule(color, 'spr-text-02');

    &:not(:last-child)::after {
      /*exclude last-child as the dot is inserted in ::after*/
      @include small-dot-mixin();
    }
  }
  .sprMedia {
    overflow: hidden;
    border-radius: 0.2rem;
    @include spr-rule(margin-top, margin-3);
    @include spr;

    /*link media overrides*/

    &.linkMedia,
    &.linkVideoMode {
      height: $linkMediaContHeight;
      @include full-width();
      border-radius: 0.5 * $spacer;
      border: 1px solid $chalk;
      .msgLinkContainer,
      .msgVideoContainer {
        border: unset;
        @include full-width();
        height: 100% !important;
        .videoImgPreview {
          @include spr-rule(border-radius, borderRadius-8);
        }
      }
      .msgVideoContainer {
        &.presentationView {
          a {
            height: 100%;
          }
        }
      }
      .titleClassName {
        margin-bottom: 0.2rem;
        @include txt-h6();
      }
      .descriptionClassName,
      .sourceClassName {
        line-height: 1.7rem;
        @include txt-bd5();
      }
      .sourceClassName {
        margin-top: 0.4rem;
      }
      .imgClassName {
        width: $linkMediaWidth;
        height: $linkMediaContHeight;
      }
      .linkContainer {
        @include full-height();
      }
    }

    &.documentMedia {
      height: 4 * $spacer;
      border: solid 1px $salt;
      @include full-width();
      @include media('>lg') {
        width: $doc-attachment-width;
      }
      @include media('<sm') {
        @include full-width();
      }
      .msgMediaDocSection {
        padding: $spacer;
        background-color: $white;
        .msgMediaDocName {
          font-size: $font-size-xsmall;
        }
      }
    }
    .mediaImgContent {
      object-fit: cover;
      width: calc(#{$engagement-entity-max-width} - #{2 * $entity-padding});
      height: 100%;
      @include spr-rule(border-radius, borderRadius-8);
    }
    .mediaContWrapper {
      max-height: $media-max-height;
      margin-right: 0 !important;
      height: 100%;
    }
    .msgVideoContainer {
      height: 30 * $spacer;
      .videoImgPreview {
        @include spr-rule(border-radius, borderRadius-8);
      }
      .videoThumbPlay {
        @include size(5 * $spacer);
      }
    }
  }

  .mediaContainer {
    height: 100%;
    @include flex-container($flow: column);
    .mediaCaption {
      @include spr-rule(color, 'spr-text-02');
      @include txt-bd5();
    }
    .msgMediaDetailsSection {
      padding: $spacer 0 0 0;
    }
  }

  .showOrignalMsgTxt {
    color: $active-color;
    margin-left: auto;
  }

  .engagementCardBox {
    overflow: hidden;
    padding-top: 1.4rem; /*half of padding given in xs sized engagement entity*/
    padding-bottom: 1.4rem;
    @include engagementCardBox;
  }

  .eventDetail__body--feed {
    @include mainContentResponsive(3.2rem, 1.6rem);
  }

  .msgContentText img {
    width: 100%;
  }

  .msgFooter {
    @include media('>md') {
      border-top: 0.1rem solid;
      @include spr-rule(border-color, spr-border-02);
    }
  }
  .msgHdrCont {
    @include spr-rule(margin-bottom, margin-4);
    margin-bottom: $msgHdr-margin;
  }

  .postStaticsWrapper {
    @include media('>md') {
      background-color: transparent;
      margin-top: 1.2rem;
    }
    @include media('<sm') {
      border-radius: 0.25 * $spacer;
      background-color: $postStats-bg-clr;
      padding: $spacer 2 * $spacer;
    }
    .statisticsContainer {
      width: inherit;
    }
  }

  .engagementEntityBox {
    border: 0.1rem solid $powder;
    @include spr-rule(border-radius, borderRadius-12);
    padding: 2 * $spacer;
  }

  .engagementEntitySwagSection {
    .replied {
      background-color: $swag-replied;
    }
    .reply_scheduled {
      background-color: $swag-replied;
    }
  }
}

