unused-classes.contentFadeInEnter:
 {
  animation: fadeInAnimation ease 250ms;
  animation-iteration-count: 1;
  animation-fill-mode: forwards;
}

unused-classes.closeContainer:
 {
  top: $universal-bar-height + $spacer;
  right: 4 * $spacer;

  .closeButton {
    height: 5rem !important;
    width: 5rem !important;
  }

  &:hover {
    .closeText {
      color: white;
    }
  }
}

unused-classes.detailPaneActions:
 {
  .msgActions__item {
    @include spr-rule(background-color, 'spr-ui-01');
    border: 1px solid;
    @include spr-rule(border-color, 'spr-clr-grey');
    @include spr-rule(border-radius, borderRadius-6);
    @include spr-rule(padding-top, padding-2);
    @include spr-rule(padding-bottom, padding-2);
    @include spr-rule(padding-left, padding-3);
    @include spr-rule(padding-right, padding-3);
  }
}

unused-classes.asset-notif-spread:
 {
  display: flex;
  align-items: stretch;
  justify-content: space-between;
}

unused-classes.postDetail__container:
 {
  border-top: 0.2rem solid $obsidian;
  max-height: 46rem;
  overflow-y: auto;
  .sprMedia {
    @include size(20rem);
  }
  @include media('<sm') {
    padding: 0.8rem !important;
  }
}

unused-classes.errorMsgContainer:
 {
  background-color: rgba(255, 96, 96, 0.03);
  max-height: $error-cont-height;
  .workflowPropValue {
    color: $error-color;
  }
  .errorText {
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
  }
  @include media('<sm') {
    border: $error-cont-border;
    border-radius: 0.4rem;
    margin-bottom: $error-cont-mobile-spacing;
  }
}

unused-classes.suggestionMsgSection:
 {
  border-radius: 0.5 * $spacer;
  max-width: $suggestion-section-width;
  padding: 2 * $spacer;
  .engagementEntity {
    border: 0;
    .engagementEntityContent {
      padding: 0;
    }
  }
  .msgContent {
    .sprMedia {
      width: $msg-content-width;
      height: auto;
    }
  }
  .asset-card {
    padding: $asset-card-padding;
    width: $asset-card-width;
  }
  .samAssetLinkSec {
    padding: $link-asset-padding;
  }
  .textContainer {
    padding: $text-cont-padding;
  }
  .samPostUserSection {
    padding: $sam-post-user-section-padding;
  }
  .samAssetLink {
    margin-left: $link-margin;
  }
  .assetWrapper {
    border: unset;
    .assetView {
      .assetFooter {
        display: none;
      }
    }
  }
  .msgFooter {
    display: none;
  }
  .msgProfileName {
    font-size: $suggested-profile-name-font-size;
  }

  .msgHdrCont {
    margin-bottom: $suggested-content-hdr-spacing;
  }
}

unused-classes.msgConversationContainer:
 {
  .convFooterContainer {
    .childViewToggle {
      color: $active-color !important;
    }

    .replyConversationIcon {
      fill: $active-color;
    }
  }

  .replyConversationIcon {
    fill: $active-color;
  }

  .hasThreadConnector.isHighlighted:before {
    border-color: $active-color;
  }

  .prevLabelBtn.showThreadConnector:before,
  .nextLabelBtn.showThreadConnector:before,
  .prevNextLabelBtn.showThreadConnector:before {
    border: 0;
  }

  .is-active > .actionIconClass {
    fill: $active-color;
  }

  .originalPostTag {
    @include txt-bd5();
    background-color: $granite;
    color: $snow;
  }

  .engagementEntityContainer {
    width: auto !important;
    padding: 0 !important;

    .msgRightSection {
      height: auto;
    }

    .msgContentSection {
      width: auto;
    }

    .msgVideoContainer {
      height: 24rem;
    }

    .profileHeaderContainer {
      margin-bottom: $spacer;

      .msgActions {
        margin-top: $spacer;
        margin-right: 0;
        visibility: visible;
      }
    }

    .sprMedia {
      overflow: hidden;
      border-radius: 0.2rem;
    }
  }
}

unused-classes.postDetail__container:
 {
  border-top: 0.2rem solid $obsidian;
  max-height: 46rem;
  overflow-y: auto;
  .sprMedia {
    @include size(20rem);
  }
  @include media('<sm') {
    padding: 0.8rem !important;
  }
}

unused-classes.aM-assetImageEditor:
 {
  .imageEditor {
    left: $nav-bar-width !important; /*classes in imageEditor are applied by id #sprImageEditor*/
  }
}

unused-classes.rightSection:
 {
  :global {
    .resonance {
      @include spr-rule(margin-bottom, margin-2);
    }

    .resonance__progress__container {
      background-color: $azure;
      height: 0.5rem;
      border-radius: $resonanceBar-border-radius;
      @include spr-rule(width, width-full);
    }

    .resonance__progress {
      background-color: $green;
      border-radius: $resonanceBar-border-radius;
      @include full-height;
    }
  }
}

unused-classes.widgetContainer:
 {
  width: $widget-container-width;
  top: 3 * $spacer;
  left: calc(50% - (#{$homepage-cards-width} / 2) - #{$widget-padding * 2} - #{$widget-container-width});
  max-height: 95%;
  :global {
    .resonance {
      @include spr-rule(margin-bottom, margin-2);
    }

    .resonance__progress__container {
      background-color: $azure;
      height: 0.5rem;
      border-radius: $resonanceBar-border-radius;
      @include spr-rule(width, width-full);
    }

    .resonance__progress {
      background-color: $green;
      border-radius: $resonanceBar-border-radius;
      @include full-height;
    }
  }
}

unused-classes.assetTile:
 {
  &:hover {
    @include spr-rule(box-shadow, spr-shadow-01);
    transition: box-shadow 0.1s linear;
  }
  &:not(:last-child) {
    margin-right: $spacer * 2;
  }
}

unused-classes.insightBodyContainer:
 {
  .customBrandCreationForm {
    /*have to use !important to override styles from file: core/containers/fullPageForm/fullPageForm.mod.scss*/
    top: 0 !important;
    height: 100vh !important;
    width: calc(100% - #{$nav-bar-width}) !important;
    left: $nav-bar-width;
  }
}

unused-classes.properties-section:
 {
  .sprFormFieldTitle {
    /*space-ui styles is over-riding distributes form-fields, remove this once that is fixed*/
    @include txt-bd5();
  }
}

unused-classes.account-selector-section:
 {
  .popoverSelectMenu {
    border-radius: 0 0 2.2rem 2.2rem;
    overflow: hidden;
    @include spr-rule(border-top, 'borderWidth-0');
    @include spr-rule(margin-top, 'margin-0', true);
    @include spr-rule(border-color, 'spr-border-02');
    box-shadow: 0 1.2rem 3.4rem 0 rgba(0, 0, 0, 0.1);
  }

  .Select-input {
    height: 4.4rem !important;
    @include spr-rule(font-size, 'fontSize-13');
    @include spr-rule(font-weight, 'fontWeight-500');
    display: inline-flex !important;
  }

  .account-card:hover {
    .closeIcon {
      display: flex;
    }
  }

  .account-card:first-child {
    @include spr-rule(margin-left, 'margin--2');
  }

  .account-selector-closed {
    border-radius: 2.2rem;
  }

  .account-selector-opened {
    border-radius: 2.2rem 2.2rem 0 0;
  }

  .Select-chevron {
    top: 1.4rem;
    right: 1.6rem;
  }

  .rotate {
    transform: rotate(180deg);
  }

  .Select-loading-zone {
    top: 0.4rem;
    right: 3.2rem;
  }
}

unused-classes.publish__editor:
 {
  font-size: 1.8rem;
  overflow: visible;
  padding: 0 !important; /*todo: why not spr-rule*/
  line-height: 1.5;
}

unused-classes.customLayoutContainer:
 {
  :global {
    .layout-row-cont {
      box-shadow: none;
      padding: 0;
    }
    .customEditor {
      margin-top: 2.4rem;
      margin-left: $spacer;
      margin-right: $spacer;
      &:first-child {
        margin-top: 0;
      }
      .layout-row-cont {
        box-shadow: inset 0 0 0.4rem 0 $smoke;
        padding: $spacer * 3;
        margin-bottom: $spacer * 3;

        @include media('<lg') {
          box-shadow: none;
          padding: 0;
        }
      }
    }
  }
}

unused-classes.convIndent:
 {
  display: flex;
  fill: $granite;
  margin-top: 0.8rem;
  height: 4 * $spacer;
  align-items: center;
}

unused-classes.stream-qA-popover-cnt:
 {
  .quickActionPopupC {
    background-color: $white;
    box-shadow: $quickAction-popup-cont-shadow;
    height: 28rem;
    width: 27rem;
    overflow-y: auto;
  }
}

unused-classes.emptyStreamContainer:
 {
  @include media('>md') {
    width: $engagement-entity-width;
    background-color: $white;
    border: 0;
    @include spr-rule(border-radius, borderRadius-6);
    .empty_stream_msg {
      .icon {
        @include size($empty-stream-icon-width, $empty-stream-icon-height);
      }
    }
  }
}
