@import "lite/component";

$acc-card-height: 8.1rem;
$acc-card-padding: 2.5 * $spacer;

$acc-img-width: 4rem;
$acc-card--business-category-width: 11rem;
$acc-det-margin: $spacer;

$acc-actions-padding: 1rem;

$acc-card--det-width: 21rem;

$acc-card-deactive-bg-color: rgba(8, 40, 204, 0.02);

.acc-card-lg {
  border-bottom: 0.1rem solid $ivory;
  height: $acc-card-height;
}

.acc-card--actions-lg {
  padding: $acc-actions-padding;
}

.acc-card--icn-sm {
  @include size($icon-size-x2large);
}

.acc-card--more-icn {
  transform: rotate(90deg);

  .fill-icon-button:hover {
    fill: $active-color;
  }
}

.acc-card--bc {
  width: $acc-card--business-category-width;
}

.acc-card-deactive {
  background-color: $acc-card-deactive-bg-color;
}

//common css
.acc-card-c {
  border: 0.1rem solid $ivory;
  margin-bottom: 3 * $spacer;
  padding: $acc-card-padding 1.2rem $acc-card-padding $acc-card-padding;
  @include size(100%);

  &.dReason {
    height: $acc-card-height + 3.4rem;
  }

  @include media("<sm") {
    margin: 0;
    border: 0;
    border-bottom: 0.1rem solid $chalk;
  }
}

.acc-card--bdy {
}

.acc-card--img {
  margin-right: $acc-det-margin;
  @include size($acc-img-width);
  img {
    border-radius: 50%;
    @include full-space();
  }
  .acc-card--icn {
    border: solid 1px $ivory;
    @include full-space();
  }
}

.accDelImgName {
  display: flex;
}

.acc-card--icn {
  fill: $smoke;
}

.acc-card--txt,
.acc-card--subTitle {
  display: block;
  @include text-overflow();
}

.acc-card--ftr {
  //only used in mobile
  right: 0;
  top: 2.25 * $spacer;
}

.acc-card--actions {
  background-color: transparent;
  border: 0;
  @include size(3.2rem);
  text-align: center;

  .acc-card--icn {
    fill: $soapstone;
  }

  &:hover {
    .acc-card--icn {
      fill: $active-color;
    }
  }
  &:focus {
    background-color: $water;
  }
}

.acc-card-c--dBy {
  @include media("<md") {
    font-size: $font-size-xsmall;
  }
}

.acc-card-ftr-icn {
  @include size(1rem);
  @include media(">md") {
    @include size($icon-size-small);
  }
}

$in-approval-clr: #ffc107;
$rejected-clr: #ff6060;
$approved-clr: #20b78c;

.acc-card-ftr-icn-sch {
  fill: $in-approval-clr;
}

.acc-card-ftr-icn-aud {
  fill: $approved-clr;
}

.acc-card-ftr-icn-rej {
  fill: $rejected-clr;
}

.acc-card-ftr-txt-rej {
  color: $rejected-clr;
}

.acc-card--txt {
  &:hover {
    color: $active-color;
  }
}

.acc-card-action-button {
  background-color: transparent;
}

