$spacer: 0.8rem;
$universal-bar-height: 6.4rem;

//to override overflow-x: hidden & overflow-y: auto by .msgConversations of hierarchicalConversation.scss
.msgConversation {
  overflow: visible !important;
}

.msgConversationBox {
  padding: 12.5 * $spacer 0;

  :global {
    .modal-dialog {
      width: 60rem;
    }

    .modal-content {
      border-radius: $spacer / 2;
    }

    //to override overflow: hidden by .msgConversationContainer of hierarchicalConversation.scss
    .msgConversationContainer {
      overflow: visible;
    }

    .sprInMsg {
      position: relative;
      top: 0;
      left: 0;
      transform: translate(0);
    }
  }
}

.closeContainer {
  top: $universal-bar-height + $spacer;
  right: 4 * $spacer;

  .closeButton {
    height: 5rem !important;
    width: 5rem !important;
  }

  &:hover {
    .closeText {
      color: white;
    }
  }
}

:global {
  #sprModalContainer > div[role="dialog"]:first-child {
    z-index: 9999;
  }
}

.chatContainer {
  max-height: 60vh;
}

