  padding: 0 $spacer * 2;
}

.footer {
  height: 5rem;
  @include spr-rule(padding-bottom, padding-4);
  @include flex-container($align-items: center, $justify: flex-end);

  :global {
    .btn-secondary {
      margin-right: 1rem; //todo: not correct
    }
  }
}

.entityEditor {
  padding-top: $spacer * 4;
}

.editor {
  @include media("<lg") {
    padding-left: $spacer * 4;
    padding-right: $spacer * 2;
  }
  :global {
    .layout-row-cont {
      box-shadow: inset 0 0 0.4rem 0 $smoke;
      padding: $spacer * 3;

      @include media("<lg") {
        box-shadow: none;
        padding: 0;
      }
    }
  }

  .layoutContainer {
    padding: $spacer * 4 0;
    &:first-child {
      padding-top: 0;
      :global {
        .layout-row-cont {
          box-shadow: none;
          padding: 0;
        }
      }
    }
    @include media("<lg") {
      padding: $spacer * 2 0;
    }
  }
}

.flexItemHalf {
  width: 50%;
  @include media("<lg") {
    width: 100%;
  }
}

.icon {
  @include size($spacer * 4);
}

.status {
  max-width: 84rem;
  margin: auto;
}

.editor {
  .profileImageBlock {
    :global {
      .form-control {
        border-radius: 0.2rem;
      }
    }
  }
}

.customLayoutContainer {
  :global {
    .layout-row-cont {
      box-shadow: none;
      padding: 0;
    }
    .customEditor {
      margin-top: 2.4rem;
      margin-left: $spacer;
      margin-right: $spacer;
      &:first-child {
        margin-top: 0;
      }
      .layout-row-cont {
        box-shadow: inset 0 0 0.4rem 0 $smoke;
        padding: $spacer * 3;
        margin-bottom: $spacer * 3;

        @include media("<lg") {
          box-shadow: none;
          padding: 0;
        }
      }
    }
  }
}

//formFields overrides for removing the borders and keeping just the border-bottom
.editor :global {
  .Select-control {
    border: none;
    border-bottom: 0.1rem solid;
    @include spr-rule(border-color, "spr-border-02");
    border-radius: 0;
  }

  .form-control {
    border: none;
    border-bottom: 0.1rem solid;
    @include spr-rule(border-color, "spr-border-02");
    border-radius: 0;
    @include media("<lg") {
      padding-top: 0;
      padding-bottom: 0;
    }
  }

  .form-control:focus {
    border-color: $secondary-active-color;
  }

  .form-group--error {
    .Select-control {
      border-bottom: 0.1rem solid $error-color;
    }

    .form-control {
      border-bottom: 0.1rem solid $error-color;
    }

    .form-control:focus {
      border-color: $error-color;
    }
  }

  .form-control[readonly] {
    background-color: inherit;
    opacity: inherit;
    color: inherit;
    border-color: $snow;
    &:hover {
      border-color: $marble;
    }
    &:focus {
      border-color: $secondary-active-color;
    }
  }

  .is-open > .Select-control {
    border-color: $secondary-active-color;
  }

  .sprFormFieldInput {
    padding-left: 0;
    &[disabled] {
      padding-left: $spacer * 1.5;
    }
  }

  textarea.form-control {
    min-height: 5rem;
  }

  .Select--multi.Select-input-md .Select-control {
    padding-left: 0;
  }

  .Select--default.Select-input-md .Select-placeholder {
    padding-left: 0;
  }
}

