  max-width: $banner-max-width;
}

.sectionsContainer {
  @include media(">md") {
    &:not(.profileBannerContainer) {
      @include spr-rule(padding-top, padding-0);
      @include spr-rule(padding-bottom, padding-0);
      @include spr-rule(padding-left, padding-5);
      @include spr-rule(padding-right, padding-5);
    }
  }
  @include media("<md") {
    @include spr-rule(padding-left, padding-4);
    @include spr-rule(padding-right, padding-4);
  }
  .profileSettingsSection {
    @include spr-rule(border-bottom, borderWidth-0);
    .profileSettingFieldsClassName {
      @include spr-rule(padding-top, padding-6);
      @include spr-rule(padding-bottom, padding-6);
      @include spr-rule(padding-left, padding-0);
      @include full-width;
    }
  }
}

.profileBannerContainer {
  @include media("<md") {
    margin-bottom: 15 * $spacer;
  }
}

