@mixin channel-display-name {
  overflow: hidden;
  margin-left: 7px;
  color: var(--center-channel-color);
  font-family: "Open Sans";
  font-size: 12px;
  font-style: normal;
  font-weight: 600;
  line-height: 16px;
  text-overflow: ellipsis;
  white-space: nowrap;
}

@mixin channel-icon {
  width: 14px;
  height: 14px;
  color: rgba(var(--center-channel-color-rgb), 0.56);
  font-size: 14px;
}

.ActivityAndInsights {
  background-color: rgba(var(--center-channel-color-rgb), 0.04);

  .insights-body {
    padding: 24px;
    overflow-x: hidden;
    overflow-y: auto;

    @media screen and (min-width: 1680px) {
      display: flex;
      flex-direction: column;

      .card-row {
        width: 1280px;
        margin: auto;
      }
    }

    .card-row {
      &::after {
        display: block;
        height: 0;
        clear: both;
        content: " ";
      }
    }

    @media screen and (max-width: 768px) {
      padding: 16px;
    }

    .top-channels-card {
      .Card__body {
        padding-right: 12px;

        @media screen and (max-width: 1019px) {
          padding-left: 12px;
        }
      }
    }

    .top-reactions-card {
      &.small {
        @media screen and (max-width: 1279px) and (min-width: 769px) {
          width: calc(100% - 16px);
        }
      }

      .Card__header {
        padding-bottom: 16px;
      }
    }

    .top-playbooks-card,
    .top-boards-card,
    .least-active-channels-card,
    .top-threads-card {
      &.small {
        @media screen and (max-width: 1279px) and (min-width: 1020px) {
          width: calc((100% / 2) - 16px);
        }

        @media screen and (max-width: 1019px) and (min-width: 769px) {
          width: calc(100% - 16px);
        }
      }

      .Card__header {
        padding-bottom: 16px;
      }

      .Card__body {
        padding: 0 12px 12px;
      }
    }

    .top-boards-card {
      .Card__body {
        padding: 0 12px 18px;
      }
    }

    .top-playbooks-card,
    .least-active-channels-card {
      &.medium {
        @media screen and (max-width: 1279px) and (min-width: 769px) {
          width: calc(100% - 16px);
        }
      }
    }

    .top-playbooks-card,
    .top-dms-card {
      .Card__body {
        padding: 0 12px 12px;
      }
    }

    .top-channel-container {
      display: flex;

      @media screen and (max-width: 1019px) {
        flex-direction: column;
      }

      .top-channel-line-chart {
        flex: 0.67;
        margin-right: 16px;

        @media screen and (max-width: 1019px) {
          flex: 1;
          padding: 12px;
          margin-right: 0;
        }

        .col-sm-12 {
          padding: 0;

          .total-count {
            position: relative;

            .title {
              display: none;
            }

            .content {
              position: relative;
            }

            .chartjs-render-monitor {
              width: 100% !important;
              min-height: 200px !important;
              max-height: 200px;

              &:hover {
                cursor: pointer;
              }
            }
          }
        }
      }

      .top-channel-list {
        flex: 0.33;
        margin-left: 16px;

        @media screen and (max-width: 1019px) {
          flex: 1;
          margin-left: 0;
        }

        .top-channel-loading-row {
          display: flex;
          align-items: center;
          padding: 12px 12px 12px 0;
        }

        .channel-row {
          display: flex;
          align-items: center;
          padding: 10px 12px;

          &:nth-of-type(1) {
            span.horizontal-bar {
              background-color: var(--button-bg);
            }
          }

          &:nth-of-type(2) {
            span.horizontal-bar {
              background-color: var(--online-indicator);
            }
          }

          &:nth-of-type(3) {
            span.horizontal-bar {
              background-color: var(--away-indicator);
            }
          }

          &:nth-of-type(4) {
            span.horizontal-bar {
              background-color: var(--dnd-indicator);
            }
          }

          &:nth-of-type(5) {
            span.horizontal-bar {
              background-color: var(--new-message-separator);
            }
          }

          &:hover {
            background-color: rgba(var(--center-channel-color-rgb), 0.08);
            border-radius: 4px;
            text-decoration: none;
          }

          &:focus {
            text-decoration: none;
          }

          .channel-display-name {
            display: flex;
            overflow: hidden;
            flex: 1 0;

            i.icon {
              @include channel-icon;
            }

            .display-name {
              @include channel-display-name;
            }
          }

          .channel-message-count {
            display: flex;
            flex: 1 0;
            align-items: center;

            .message-count {
              display: inline-flex;
              flex: 0.2 0;
              justify-content: flex-end;
              color: rgba(var(--center-channel-color-rgb), 0.72);
              font-family: "Open Sans";
              font-size: 11px;
              font-style: normal;
              font-weight: 400;
              line-height: 16px;
              text-align: right;
            }

            span.horizontal-bar {
              height: 8px;
              margin-left: 16px;
              border-radius: 6px;
            }
          }
        }
      }
    }

    .top-reaction-container {
      display: flex;
      align-items: center;
      justify-content: center;
      margin: 0 18px;

      .bar-chart-entry {
        display: flex;
        flex: 0.2;
        flex-direction: column;
        align-items: center;
        margin-top: auto;

        &:nth-of-type(1) {
          .bar-chart-data {
            background-color: var(--button-bg);
          }
        }

        &:nth-of-type(2) {
          .bar-chart-data {
            background-color: var(--online-indicator);
          }
        }

        &:nth-of-type(3) {
          .bar-chart-data {
            background-color: var(--dnd-indicator);
          }
        }

        &:nth-of-type(4) {
          .bar-chart-data {
            background-color: var(--new-message-separator);
          }
        }

        &:nth-of-type(5) {
          .bar-chart-data {
            background-color: var(--away-indicator);
          }
        }

        .reaction-count {
          margin-bottom: 6px;
          font-family: "Open Sans";
          font-size: 11px;
          font-style: normal;
          font-weight: 600;
          line-height: 16px;
        }

        .bar-chart-data {
          width: 8px;
          margin-bottom: 6px;
          border-radius: 6px;
        }
      }
    }

    .top-thread-container {
      .top-thread-loading-container {
        padding: 0 12px;

        &:nth-of-type(2) {
          margin-top: 20px;
        }

        &:nth-of-type(3) {
          margin-top: 20px;
        }

        .top-thread-loading-row {
          display: flex;
          align-items: center;
          padding-bottom: 8px;
        }
      }

      .thread-list {
        .thread-item {
          padding: 8px 12px;

          &:nth-of-type(3) {
            .preview {
              margin-bottom: 0;
            }
          }

          &:hover {
            background: rgba(var(--center-channel-color-rgb), 0.08);
            border-radius: 4px;
            cursor: pointer;
          }
        }
      }
    }

    .top-board-container {
      margin-bottom: -3px;

      .top-board-loading-container {
        display: flex;
        align-items: center;
        padding: 0 12px;
        margin-bottom: 20px;

        &:nth-of-type(4) {
          margin-bottom: 4px;
        }

        .loading-lines {
          display: flex;
          flex: 1;
          flex-direction: column;
          margin-left: 10px;
        }
      }

      .board-list {
        .board-item {
          display: flex;
          align-items: center;
          padding: 10px 12px;

          &:hover {
            background: rgba(var(--center-channel-color-rgb), 0.08);
            border-radius: 4px;
            cursor: pointer;
            text-decoration: none;
          }

          &:focus {
            text-decoration: none;
          }

          span.board-icon {
            display: flex;
            width: 32px;
            height: 32px;
            align-items: center;
            justify-content: center;
            float: left;
            font-size: 32px;
          }

          .display-info {
            display: flex;
            overflow: hidden;
            flex-direction: column;
            margin-left: 12px;

            .display-name {
              overflow: hidden;
              margin-right: 10px;
              color: var(--center-channel-color);
              float: left;
              font-family: "Open Sans";
              font-size: 12px;
              font-style: normal;
              font-weight: 600;
              line-height: 16px;
              text-overflow: ellipsis;
              white-space: nowrap;
            }

            .update-counts {
              color: rgba(var(--center-channel-color-rgb), 0.56);
              font-family: "Open Sans";
              font-size: 12px;
              font-style: normal;
              font-weight: 400;
              line-height: 16px;
            }
          }

          .Avatars.Avatars___xs {
            margin-left: auto;
            float: right;
          }
        }
      }
    }

    .least-active-channels-container {
      .least-active-channels-loading-container {
        display: flex;
        width: 100%;
        align-items: center;
        padding: 0 12px;
        margin-top: 24px;

        &:nth-of-type(1) {
          margin-top: 10px;
        }

        &:nth-of-type(4) {
          margin-bottom: 10px;
        }
      }

      .channel-list {
        .channel-row {
          display: flex;
          align-items: center;
          padding: 10px 12px;

          &:hover {
            background: rgba(var(--center-channel-color-rgb), 0.08);
            border-radius: 4px;
            cursor: pointer;
            text-decoration: none;
          }

          &:focus {
            text-decoration: none;
          }

          .channel-info {
            display: flex;
            overflow: hidden;
            flex: 0.4;
            flex-direction: column;

            .channel-display-name {
              display: flex;
              overflow: hidden;

              i.icon {
                @include channel-icon;
              }

              .display-name {
                @include channel-display-name;
              }
            }

            .last-activity {
              color: rgba(var(--center-channel-color-rgb), 0.56);
              font-family: "Open Sans";
              font-size: 11px;
              font-style: normal;
              font-weight: 400;
              line-height: 16px;
            }
          }

          .Avatars {
            margin-left: auto;
          }
        }
      }

      .empty-state {
        margin: 38px 0;
      }
    }

    .top-playbooks-container {
      .top-playbooks-loading-container {
        padding: 0 12px;

        &:nth-of-type(1) {
          margin-top: 12px;
        }

        &:nth-of-type(2) {
          margin-top: 24px;
          margin-bottom: 24px;
        }

        &:nth-of-type(3) {
          margin-bottom: 12px;

          > div {
            &:nth-of-type(2) {
              margin-bottom: 0;
            }
          }
        }
      }

      .playbooks-list {
        .playbook-item {
          display: flex;
          align-items: center;
          padding: 10px 12px;

          &:hover {
            background: rgba(var(--center-channel-color-rgb), 0.08);
            border-radius: 4px;
            cursor: pointer;
            text-decoration: none;
          }

          &:focus {
            text-decoration: none;
          }

          &:nth-of-type(2) {
            .horizontal-bar {
              opacity: 0.7;
            }
          }

          &:nth-of-type(3) {
            .horizontal-bar {
              opacity: 0.5;
            }
          }

          .display-info {
            display: flex;
            overflow: hidden;
            flex: 0.6 0;
            flex-direction: column;

            &.run-info {
              flex: 0.4 0;
            }

            .display-name {
              overflow: hidden;
              margin-right: 10px;
              color: var(--center-channel-color);
              float: left;
              font-family: "Open Sans";
              font-size: 12px;
              font-style: normal;
              font-weight: 600;
              line-height: 16px;
              text-overflow: ellipsis;
              white-space: nowrap;
            }

            .last-run-time {
              color: rgba(var(--center-channel-color-rgb), 0.56);
              font-family: "Open Sans";
