#BillingSubscriptions__seatOverageTooltip {
  margin-left: -5px;
}

.BillingSubscriptions__tooltip.tooltip {
  font-family: "Open Sans", sans-serif;

  &.bottom {
    padding: 10px 0;
  }

  &.in {
    opacity: 1;
  }

  &.BillingSubscriptions__tooltip-left .tooltip-inner {
    left: 40%;
  }

  &.BillingSubscriptions__tooltip-right .tooltip-inner {
    right: 90%;
  }

  .tooltip-inner {
    position: relative;
    min-width: 337px;
    max-width: none;
    padding: 24px;
    border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.08);
    background-color: var(--sys-center-channel-bg);
    border-radius: 4px;
    box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
    color: var(--sys-center-channel-color);
    opacity: 1;
    text-align: left;
  }

  .tooltip-arrow {
    z-index: 2;
    border-width: 0 11px 11px;
    border-bottom-color: rgba(var(--sys-center-channel-color-rgb), 0.08);

    &::before {
      position: relative;
      top: -10px;
      left: -11px;
      display: block;
      width: 0;
      height: 0;
      border: 11px solid transparent;
      border-bottom-color: var(--sys-center-channel-bg);
      content: "";
    }
  }
}

.BillingSubscriptions__tooltipTitle {
