.BillingSummary {
  width: 332px;
  padding: 28px 32px;
  border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.08);
  margin-left: 20px;
  background-color: var(--sys-center-channel-bg);
  border-radius: 4px;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.08);
}

.BillingSummary__noBillingHistory {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
}

.BillingSummary__noBillingHistory-title {
  margin-top: 32px;
  font-size: 16px;
  font-weight: 600;
  line-height: 24px;
}

.BillingSummary__noBillingHistory-message {
  margin-top: 8px;
  color: var(--sys-center-channel-color);
  font-size: 14px;
  line-height: 20px;
}

.BillingSummary__noBillingHistory-link {
  margin-top: 16px;
  margin-bottom: 24px;
  color: var(--sys-button-bg);
  font-size: 12px;
  font-weight: 600;
}

.BillingSummary__lastInvoice {
  color: var(--sys-center-channel-color);

  hr {
    border-color: rgba(var(--sys-center-channel-color-rgb), 0.32);
    margin: 12px 0;
  }
}

.BillingSummary__lastInvoice-header {
  display: flex;
  align-items: center;
}

.BillingSummary__lastInvoice-headerTitle {
  font-size: 20px;
  font-weight: 600;
  line-height: 28px;
}

.BillingSummary__lastInvoice-headerStatus {
  display: flex;
  align-items: center;
  margin-left: auto;
  font-size: 12px;
  font-weight: 600;
  line-height: 16px;

  &.paid {
    color: var(--sys-online-indicator);
  }

  &.failed {
    color: var(--sys-error-text);
  }

  &.pending {
    color: rgba(var(--sys-center-channel-color-rgb), 0.48);
  }

  svg {
    width: 16px;
    height: 16px;
    margin-left: 4px;
    font-size: 14.4px;

    &::before {
      margin: 0 auto;
    }
  }
}

.BillingSummary__lastInvoice-date {
  margin-top: 8px;
  color: rgba(var(--sys-center-channel-color-rgb), 0.72);
}

.BillingSummary__lastInvoice-productName {
  margin-top: 32px;
  font-size: 16px;
  font-weight: 600;
  line-height: 24px;
}

.BillingSummary__lastInvoice-charge {
  display: flex;
  margin: 12px 0;

  &.total {
    font-weight: 600;
  }
}

.BillingSummary__lastInvoice-hasMoreItems {
  display: flex;
  align-items: center;
  color: #166de0;

  .BillingSummary__lastInvoice-chargeDescription {
    &:hover {
      cursor: pointer;
      text-decoration: underline;
    }
  }
}

.BillingSummary__lastInvoice-chargeAmount {
  margin-left: auto;
}

.BillingSummary__lastInvoice-partialCharges {
  color: rgba(var(--sys-center-channel-color-rgb), 0.56);
  font-size: 12px;
  line-height: 16px;

  & + .BillingSummary__lastInvoice-charge {
    margin-top: 4px;
  }
}

.BillingSummary__lastInvoice-download {
  margin-top: 32px;
  margin-bottom: 16px;
}

.BillingSummary__lastInvoice-downloadButton {
  display: flex;
  width: 100%;
  align-items: center;
  justify-content: center;
  padding: 11px 20px;
  background: var(--sys-button-bg);
  border-radius: 4px;
  color: var(--sys-button-color);

  &:hover:not(.disabled) {
    background: linear-gradient(
        0deg,
        rgba(var(--sys-center-channel-color-rgb), 0.16),
        rgba(var(--sys-center-channel-color-rgb), 0.16)
      ),
      var(--sys-button-bg);
    color: var(--sys-button-color);
    text-decoration: none;
  }

  &:active {
    background: linear-gradient(
        0deg,
        rgba(var(--sys-center-channel-color-rgb), 0.32),
        rgba(var(--sys-center-channel-color-rgb), 0.32)
      ),
      var(--sys-button-bg);
    color: var(--sys-button-color);
    text-decoration: none;
  }

  &:focus {
    box-shadow: inset 0 0 0 2px var(--sys-sidebar-text-active-border);
    color: var(--sys-button-color);
    text-decoration: none;
  }

  &.disabled {
    background: rgba(var(--sys-center-channel-color-rgb), 0.08);
    color: rgba(var(--sys-center-channel-color-rgb), 0.32);
  }

  > i {
    font-size: 18px;
    line-height: 21px;

    &::before {
      margin: 0;
    }
  }

  > span {
    margin-left: 6px;
    font-size: 14px;
    font-weight: 600;
    line-height: 14px;
  }
}

.BillingSummary__lastInvoice-billingHistory {
  display: block;
  font-size: 12px;
  font-weight: 600;
  line-height: 9.5px;
  text-align: center;
}

