      box-shadow: inset 0 0 0 1px var(--sys-error-text);
      color: var(--error-text);
    }

    &.Input_fieldset___error:focus-within {
      box-shadow: inset 0 0 0 2px var(--sys-error-text);
      color: var(--error-text);
    }

    .Input {
      height: 32px;

      &::placeholder {
        color: var(--sys-center-channel-color);
        opacity: 0.64;
      }
    }
  }
}

