.TeamFilterDropdown {
  .TeamFilterDropdownButton {
    display: flex;
    width: 100%;
    height: 32px;
    box-sizing: border-box;
    justify-content: space-between;
    padding: 1px 0 1px 4px;
    border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.16);
    background: var(--sys-center-channel-bg);
    border-radius: 4px;
    color: rgba(var(--sys-center-channel-color-rgb), 0.64);
    font-size: 12px;
    font-style: normal;
    font-weight: normal;
    line-height: 28px;

    &:hover {
      border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.48);

      .TeamFilterDropdownButton_more {
        margin-right: 0;
      }

      .TeamFilterDropdownButton_clear {
        display: block;
      }
    }

    .TeamFilterDropdownButton_text {
      max-width: 230px;
      flex-grow: 3;
      padding-left: 8px;
      overflow-x: hidden;
      text-align: left;
      text-overflow: ellipsis;
    }

    .TeamFilterDropdownButton_more {
      margin-right: 8px;
      color: rgba(var(--sys-center-channel-color-rgb), 0.6);
    }

    .TeamFilterDropdownButton_icon {
      min-width: 32px;
      height: 31px;
      border-left: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.16);
      margin-top: -1px;

      .Icon {
        display: flex;
        width: 14px;
        height: 14px;
        padding-top: 8px;
        margin-right: 0;
        margin-left: 6px;
      }
    }

    .TeamFilterDropdownButton_clear {
      display: none;
      min-width: 36px;
      height: 32px;
      padding: 9.5px 0 0 0;
      margin-top: -2px;
      color: rgba(var(--sys-center-channel-color-rgb), 0.56);
      font-size: 14px;
    }
  }

  .TeamFilterDropdownOptions {
    position: absolute;
    z-index: 1;
    display: none;
    overflow: hidden;
    min-width: 304px;
    max-width: 304px;
    max-height: 260px;
    box-sizing: border-box;
    padding: 16px 16px 0 16px;
    border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.08);
    margin-top: 2px;
    background: var(--sys-center-channel-bg);
    border-radius: 4px;
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.12);
    text-overflow: ellipsis;

    .TeamFilterDropdownOptions_list {
      max-height: 184px;
      padding-bottom: 8px;
      margin: 0 -12px 8px -16px;
      overflow-x: hidden;
      overflow-y: scroll;

      &::-webkit-scrollbar-track {
        background: rgba(var(--sys-center-channel-bg-rgb), 0);
      }
    }
  }

  .TeamFilterDropdownOptions__active {
    display: block;
  }

  .TeamFilterDropdown_checkbox {
    height: 32px;
    margin-right: 4px;
    color: rgba(var(--sys-center-channel-color-rgb), 0.8);

    input {
      position: relative;
      top: -4px;
      padding-top: 6px;
      margin-right: 8px;
      margin-left: 20px;
      opacity: 0.64;
      vertical-align: bottom;

      &:hover {
        cursor: pointer;
      }

      &:checked {
        opacity: 1;
      }
    }

    label {
      display: inline-block;
      overflow: hidden;
      width: 100%;
      height: 32px;
      padding-top: 6px;
      font-weight: normal;
      text-overflow: ellipsis;

      &:hover {
        cursor: pointer;
      }
    }

    &:hover {
      background: rgba(var(--sys-center-channel-color-rgb), 0.08);
      cursor: pointer;
    }
  }

  .TeamFilterDropdown_search {
    width: 100%;
    height: 32px;
    box-sizing: border-box;
    padding: 8px 12px;
    border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.2);
    border-radius: 4px;
    color: var(--sys-center-channel-color);
    font-size: 12px;
    font-weight: 400;
    line-height: 16px;

    &:focus {
      border: 2px solid var(--sys-button-bg);
      margin-left: -1px;
    }
  }

  .TeamFilterDropdown_reset {
    display: block;
    padding: 4px 0 8px 1px;
    color: var(--sys-link-color);
    font-size: 12px;
    font-weight: 600;
  }

  .TeamFilterDropdown_allTeams {
    padding: 4px 0 8px 1px;
    color: rgba(var(--sys-center-channel-color-rgb), 0.64);
    font-size: 12px;
    font-weight: normal;
  }

  .TeamFilterDropdown_divider {
    border-top: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.12);
    margin: 4px -16px;
  }

  .TeamFilterDropdown_empty,
  .TeamFilterDropdown_loading {
    padding-top: 8px;
    margin-bottom: 0;
    margin-left: 16px;
    color: rgba(var(--sys-center-channel-color-rgb), 0.64);
    font-weight: normal;
  }
}

