@import "utils/variables";

.AdvancedTextEditor {
  &__ctr {
    form {
      padding: unset;
    }
  }

  display: flex;
  width: 100%;
  flex-direction: row;
  align-items: end;
  padding: 0 24px;
  margin: 0 auto;
  gap: 12px;
  justify-items: stretch;

  .sidebar--right & {
    padding-top: 12px;
  }

  .ThreadViewer & {
    padding-top: 12px;
  }

  .custom-textarea {
    // fix for a miscalculation due to the heavily overwritten custom-textarea class
    height: 0;
    box-shadow: none;

    &:focus {
      box-shadow: none;
    }

    &.custom-textarea--emoji-picker,
    &.textbox-preview-area {
      height: auto;
      padding-right: 16px;
    }
  }

  &__body {
    position: relative;
    max-width: 100%;
    flex: 1;
    border: 2px solid rgba(var(--center-channel-color-rgb), 0.16);
    border-radius: 4px;

    &:focus-visible,
    &:focus-within,
    &:focus-visible:hover,
    &:focus-within:hover {
      border: 2px solid rgba(var(--center-channel-color-rgb), 0.32);
      outline: none;
      outline-offset: 0;
    }

    &:hover {
      border: 2px solid rgba(var(--center-channel-color-rgb), 0.24);
    }

    &[disabled] {
      &:hover {
        border: none;
      }
    }
  }

  &__priority {
    display: flex;
    align-items: center;
    padding: 14px 16px 0;
    gap: 6px;

    &-ack {
      display: flex;
      align-items: center;
      color: var(--online-indicator);

      > span {
        margin-left: 4px;
        font-size: 11px;
        font-weight: 600;
      }

      &-tooltip {
        max-width: 230px;
      }
    }

    button.close {
      display: flex;
      align-items: center;
      justify-content: center;
      margin-top: -1px;
      color: rgb(var(--center-channel-color));
      font-size: 17px;
      opacity: 0.56;
      visibility: hidden;

      &:hover {
        opacity: 0.72;
      }
    }

    &:hover {
      button.close {
        visibility: visible;
      }
    }
  }

  &__action-button {
    display: flex;
    width: 32px;
    height: 32px;
    flex-shrink: 0;
    align-items: center;
    justify-content: center;
    padding: 0;
    border: none;
    background: transparent;
    border-radius: 4px;
    color: rgba(var(--center-channel-color-rgb), 0.56);
    cursor: pointer;
    fill: currentColor;

    &:hover {
      background: rgba(var(--center-channel-color-rgb), 0.08);
      color: rgba(var(--center-channel-color-rgb), 0.72);
      fill: currentColor;
      text-decoration: none;
    }

    &.hidden {
      visibility: hidden;
    }

    & + input {
      position: absolute;
      top: 0;
      right: 0;
      width: 100%;
      height: 100%;
      margin: 0;
      cursor: pointer;
      direction: ltr;
      font-size: 23px;
      opacity: 0;
      pointer-events: none;
    }
  }

  &__action-button--active {
    background: rgba(var(--button-bg-rgb), 0.08);
    color: rgb(var(--button-bg-rgb));
    fill: currentColor;
  }

  &__icon {
    display: inline-flex;
    width: 100%;
    height: 100%;
    align-items: center;
    justify-content: center;
    font-family: "compass-icons";
    font-size: 18px;
  }

  &__cell {
    position: relative;
    display: flex;
    flex-direction: column;
    vertical-align: top;
  }

  &__helper-text {
    display: inline-block;
    margin: 8px 0 4px;
    color: rgba(var(--center-channel-color-rgb), 0.64);
    font-family: "Open Sans", sans-serif;
  }

  &__footer {
    position: relative;
    display: flex;
    flex-direction: column;
    padding: 0 24px;
    font-size: 13px;
    gap: 8px;

    .help__text {
      text-align: right;

      a,
      button {
        margin-left: 10px;
      }
    }

    &--has-error {
      padding: 6px 0 0;
      color: var(--error-text);
    }

    .post-error {
      position: absolute;
      top: 4px;
      display: inline-block;
      margin-bottom: 0;
      font-size: 0.85em;
      font-weight: normal;
    }
  }
}

@media screen and (max-width: 768px) {
  .AdvancedTextEditor {
    padding: 0;

    &__body {
      border-right: none;
      border-left: none;
      border-radius: 0;

      &:focus-visible,
      &:focus-within,
      &:focus-visible:hover,
      &:focus-within:hover,
      &:hover {
        border-right: none;
        border-left: none;
      }
    }

    .ThreadViewer & {
      .msg-typing:empty {
        display: none;
      }
    }
  }

  .sidebar--right .post-create__container {
    form {
      padding: 0;
    }
  }
}

.msg-typing {
  display: block;
  overflow: hidden;
  height: 20px;
  margin-bottom: 5px;
  font-size: 0.95em;
  opacity: 0.7;
  text-overflow: ellipsis;
  white-space: nowrap;
}

