.Card {
  border: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
  background-color: var(--center-channel-bg);
  border-radius: 4px;
  box-shadow: 0 2px 3px rgba(0, 0, 0, 0.08);
  transition: box-shadow 0.3s ease-in-out;

  & + .Card {
    margin-top: 16px;
  }

  &.expanded {
    box-shadow: 0 8px 24px rgba(0, 0, 0, 0.12);
  }

  &:not(.expanded):hover {
    border-color: rgba(var(--center-channel-color-rgb), 0.16);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.12);
    transition-duration: 0.15s;
  }

  &.console {
    width: 100%;
    border: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.08);
    margin-bottom: 20px;
    background-color: var(--sys-center-channel-bg);
    border-radius: 2px;
    box-shadow: 0 2px 3px rgba(0, 0, 0, 0.08);
    color: var(--sys-center-channel-color);

    .Card__header {
      display: flex;
      align-items: center;
      padding: 28px 32px 24px 32px;
      border-bottom: 1px solid rgba(var(--sys-center-channel-color-rgb), 0.08);

      .text-top {
        font-size: 16px;
        font-weight: 600;
        line-height: 24px;
      }

      .text-bottom {
        margin-right: 24px;
        font-size: 14px;
        line-height: 20px;
      }

      .content-button {
        height: 32px;
        padding: 10px 16px;
        border: none;
        margin-left: auto;
        border-radius: 4px;
        font-size: 12px;
        font-weight: 600;
        line-height: 9.5px;
        white-space: nowrap;

        &.primary {
          background: var(--sys-button-bg);
          color: var(--sys-button-color);
        }
      }

      .Card__hr {
        display: none;
      }
    }

    .Card__body {
      overflow: visible;

      &.expanding {
        transition: none;
      }

      &.expanded {
        margin: 28px 32px;
      }
    }
  }
}

.Card__header {
  hr.Card__hr {
    border-color: rgba(var(--center-channel-color-rgb), 0.08);
    margin: 0 12px;
  }
}

.Card__body {
  overflow: hidden;
  height: 0;
  margin: 0 12px;

  &.expanding {
    transition-duration: 0.3s;
    transition-property: height, margin;
    transition-timing-function: ease-in-out;
  }

  &.expanded {
    margin: 12px;
  }

  &.expanded:not(.expanding) {
    height: auto;
  }
}

@media screen and (max-width: 768px) {
  .Card + .Card {
    margin-top: 12px;
  }
}

