@import "utils/variables";
@import "utils/mixins";

.RequestBusinessEmailModal {
  height: 320px;

  &.modal-dialog {
    margin-top: calc(50vh - 350px) !important;
  }

  .modal-content {
    padding: 0 !important;
    border-color: rgba(var(--center-channel-color-rgb), 0.16);
    background: var(--center-channel-bg);
    border-radius: 8px;
    color: var(--center-channel-color);
  }

  .modal-header {
    .close {
      &:hover,
      &:active,
      &:focus,
      &:active:focus {
        background-color: rgba(var(--center-channel-color-rgb), 0.08);
        color: rgba(var(--center-channel-color-rgb), 0.72);
        opacity: 1;
      }

      top: 6px;
      right: 4px;
      width: 4rem;
      height: 4rem;
      border-radius: 4px;
      color: rgba(var(--center-channel-color-rgb), 0.56) !important;
      font-family: "Open Sans", sans-serif;
      font-size: 32px;
      font-weight: 400;
    }

    height: 38px;
    padding: 0;
    border: 0;
    background: var(--center-channel-bg) !important;
    border-radius: 8px;
    color: var(--center-channel-color);
  }

  .modal-body {
    display: flex;
    overflow: hidden;
    width: 100%;
    height: calc(100% - 38px);
    flex-direction: column;
    padding: 0;

    .GenericModal__body {
      height: 100%;
      padding: 0 24px 24px 24px;

      .container-footer {
        bottom: 0;
        height: 36px;
      }
    }

    .request-business-email-input {
      height: 34px !important;
      border: 0 !important;
      border-radius: 0 !important;
    }

    .start-trial-email-title {
      margin-bottom: 22px;
      color: var(--center-channel-color);
      font-size: 22px;
      font-weight: 600;
      line-height: 28px;
    }

    .start-trial-email-description {
      margin-bottom: 16px;
      color: var(--center-channel-color);
      font-size: 14px;
      font-weight: 400;
      line-height: 20px;
    }

    .start-trial-email-disclaimer {
      margin-top: 56px;
    }

    .start-trial-button {
      display: flex;

      button {
        @include primary-button;

        margin-left: auto;
      }
    }
  }

  .modal-centered {
    text-align: center;
  }

  .modal-footer {
    padding: 12px 24px 24px;
    border: none;
    border-radius: 4px;
  }
}

