.container-slider {
  position: relative;
  overflow: hidden;
  width: 100%;
  min-width: 400px;
  height: 100%;
  min-height: 400px;

  @media screen and (max-width: 700px) {
    .container-slider {
      margin: 10px;
    }
  }

  .slide {
    position: absolute;
    z-index: 0;
    width: 100%;
    height: 100%;
    opacity: 0;
    transition: opacity ease-in-out 0.4s;
  }

  .slide img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  .active-anim {
    z-index: 1;
    opacity: 1;
  }

  .chevron-button {
    z-index: 2;
    margin-top: 120px;
    color: var(--center-channel-color);
    cursor: pointer;
    font-size: 24px;

    &.disabled {
      opacity: 0.5;
    }

    &.chevron-right {
      position: absolute;
      right: 0;
    }

    &.chevron-left {
      position: absolute;
      left: 0;
    }
  }

  .container-footer {
    position: absolute;
    z-index: 2;
    bottom: 3%;
    display: flex;
    width: 100%;
    align-items: center;
    justify-content: flex-start;

    .container-buttons {
      margin-left: auto;

      .btn-slide {
        position: absolute;
        display: flex;
        width: 60px;
        height: 20px;
        align-items: center;
        justify-content: center;
        border: 1px solid rgba(34, 34, 34, 0.287);
        cursor: pointer;
        font-size: 12px;
        font-weight: 600;
      }

      .disabled {
        opacity: 0.5;
        text-decoration: none;
      }

      .prev,
      .next {
        left: 20px;
        transform: translateY(-60%);
      }
    }

    .container-dots {
      display: flex;

      .dot {
        width: 8px;
        height: 8px;
        margin: 0 5px;
        background: rgba(var(--button-bg-rgb), 0.25);
        border-radius: 50%;
      }

      .dot.active {
        width: 6px;
        height: 6px;
        margin-top: 1px;
        background: var(--button-bg);
        box-shadow: 0 0 0 2px rgba(var(--button-bg-rgb), 0.4);
      }
    }
  }
}

