.file-preview-modal {
  padding-left: 0 !important;

  &__body {
    height: 100vh;
    padding: 0;
  }

  &__main-ctr {
    display: flex;
    width: 100%;
    height: 100%;
    flex-direction: column;
  }

  &__title {
    background: black;
  }

  &__content {
    display: flex;
    height: calc(100vh - 72px);
    align-items: center;
    justify-content: center;
    padding: 48px;
