  &__content-scrollable {
    padding: 0;
  }

  &__scrollable {
    overflow: auto;
    width: 100%;
    height: calc(100vh - 72px);
    padding: 48px 0 72px 0;
    margin: 0 auto;

    @media screen and (max-width: 768px) {
      height: calc(100vh - 148px);
      padding: 0;
    }
  }

  &__zoom-bar {
    .modal .modal-image & {
      background-color: rgba(61, 60, 64, 0.56);
    }
  }

  .modal.fade & {
    transform: translate(0, 0);
  }
}

