@import "utils/variables";

.InviteResultTable {
  width: 100%;
  max-height: 400px;
  overflow-y: auto;

  .table-header {
    display: flex;
    height: 40px;
    border-top: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
    border-bottom: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
    color: var(--center-channel-color);
    font-size: 14px;

    .people-header,
    .details-header {
      display: flex;
      width: 50%;
      align-items: center;
      margin: 12px;
      font-weight: bold;
    }

    span {
      display: flex;
    }
  }
}

.InviteResultRow {
  display: flex;

  .username-or-icon {
    display: flex;
    width: 50%;
    align-items: center;

    .email {
      margin: 0 4px 0 8px;
      overflow-wrap: anywhere;
      word-break: break-word;
    }

    .name {
      margin: 0 4px 0 8px;
    }

    .mail-icon {
      display: inline-block;
      width: 36px;
      height: 36px;
      padding: 3px;
      margin: 12px 0 12px 12px;
      background: var(--button-bg);
      border-radius: 18px;

      svg {
        width: 24px;
        height: 24px;
        margin: 3px;
        fill: var(--button-color);
      }
    }

    .alert-icon {
      display: inline-block;
      width: 36px;
      height: 36px;
      padding: 3px;
      margin: 12px 0 12px 12px;
      background: var(--dnd-indicator);
      border-radius: 18px;

      svg {
        width: 28.8px;
        height: 28.8px;
        margin: 0.6px;
        fill: var(--button-color);
      }
    }

    .Avatar {
      margin: 12px 0 12px 12px;
    }

    .Tag {
      margin-left: 4px;
    }
  }

  .reason {
    width: 50%;
    align-items: center;
    padding: 12px;
  }
}

.invitation-modal-confirm {
  h2 {
    margin-bottom: 13px;
    color: rgba(var(--center-channel-color-rgb), 0.72);
    font-size: 14px;
    font-weight: 600;
  }

  &--not-sent {
    .InviteResultTable {
      .table-header {
        background: rgba(238, 179, 0, 0.2);
      }
    }
  }

  &--sent {
    .InviteResultTable {
      .table-header {
        background: rgba(0, 131, 89, 0.2);

        svg {
          width: 18px;
          height: 18px;
          fill: var(--center-channel-color);
        }
      }
    }
  }
}

