        margin-top: 0.6em;
      }

      p + ul,
      p + ol {
        margin-top: 0.6em;
      }

      img {
        max-width: 450px;
      }

      p {
        width: 100%;
        margin: 0;
        color: rgba(var(--center-channel-color-rgb), 0.72);
        font-size: 18px;
        font-weight: 400;
        line-height: 28px;
        white-space: pre-wrap;
        word-break: break-word;
      }
    }
  }
}

@media screen and (min-width: 1680px) {
  .login-body .login-body-content {
    .login-body-message,
    .login-body-card {
      width: 610px;
    }

    .login-body-message .login-body-message-title {
      padding-right: 110px;
      margin-top: 48px;
    }
  }
}

@media screen and (max-width: 1199px) {
  .login-body {
    .login-body-alternate-link {
      padding-right: 24px;
    }

    .login-body-content {
      flex-direction: column;

      .login-body-message,
      .login-body-card {
        width: 640px;
      }

      .login-body-message {
        align-self: center;
        padding: 24px;

        .login-body-message-title {
          padding-right: 180px;
          font-size: 64px;
          line-height: 76px;
        }

        .login-body-message-subtitle {
          margin: 0;
        }

        .login-body-message-svg {
          display: none;
        }

        &.custom-branding {
          display: none;
        }
      }

      .login-body-card {
        border: none;
        margin: 0;
        background-color: unset;
        box-shadow: none;

        .login-body-card-content {
          padding: 16px 24px;

          .login-body-card-title {
            display: none;
          }

          .login-body-card-banner {
            margin: 0 0 32px;
          }

          .login-body-card-form {
            .login-body-card-form-input {
              margin-top: 0;
            }

            .login-body-card-form-input,
            .login-body-card-form-password-input {
              background-color: var(--center-channel-bg);
            }
          }
        }

        &.custom-branding {
          .login-body-card-content {
            .login-body-card-title {
              display: block;
              font-size: 32px;
              line-height: 40px;
            }

            .login-body-custom-branding-markdown,
            .login-body-message-subtitle {
              display: block;
              margin: 0 0 32px;
              text-align: left;
            }
          }
        }
      }
    }
  }
}

@media screen and (max-width: 699px) {
  .login-body {
    margin: auto 0;

    .login-body-content {
      min-width: 375px;

      .login-body-card {
        width: 100%;
      }

      .login-body-message {
        width: auto;
        align-self: flex-start;
        padding: 24px;

        .login-body-message-title {
          max-width: 271px;
          padding-right: 0;
          font-size: 45px;
          line-height: 56px;
        }
      }
    }
  }
}

