.PaymentForm {
  padding-right: 96px;
  padding-left: 96px;
  margin: 0 auto;

  .form-row {
    display: flex;
    width: 100%;
    margin-bottom: 24px;
    font-weight: normal;
  }

  .form-row-third-1 {
    .DropdownInput {
      z-index: 99999;
      margin-top: 0;
    }

    width: 66%;
    max-width: 288px;
    margin-right: 16px;
    font-weight: normal;
  }

  .form-row-third-2 {
    width: 34%;
    max-width: 144px;
  }

  .section-title {
    margin-bottom: 24px;
    color: rgba(var(--center-channel-color-rgb), 0.72);
    font-size: 16px;
    font-weight: 600;
    text-align: left;
  }

  .DropdownInput {
    position: relative;
    z-index: 999999;
    height: 36px;
    margin-bottom: 24px;
    font-weight: normal;

    .DropDown__control {
      min-height: 0;
      background-color: var(--center-channel-bg) !important;

      .DropDown__value-container > div {
        color: var(--center-channel-color) !important;
      }
    }

    .DropDown__menu {
      background-color: var(--center-channel-bg) !important;
      box-shadow: 0 0 0 1px var(--center-channel-color),
        0 4px 11px var(--center-channel-color) !important;
    }
  }

  .full-width {
    width: 100%;
  }

  input {
    font-size: 14px;
  }

  input:-webkit-autofill,
  input:-webkit-autofill:hover,
  input:-webkit-autofill:focus,
  input:-webkit-autofill:active {
    -webkit-box-shadow: 0 0 0 30px var(--center-channel-bg) inset !important;
    -webkit-text-fill-color: var(--center-channel-color) !important;
  }

  .Input_fieldset {
    height: 40px;
    padding: 2px 1px;
    background: var(--center-channel-bg);

    .Input_wrapper {
      margin: 0;
    }

    &.Input_fieldset___legend {
      > legend {
        margin-left: 11px;
      }
    }

    &.Input_fieldset:focus-within {
      padding-top: 2px;
      box-shadow: inset 0 0 0 2px var(--button-bg);
      color: var(--button-bg);
    }

    &.Input_fieldset___error {
      padding-top: 1px;
      padding-bottom: 1px;
      box-shadow: inset 0 0 0 1px var(--error-text);
      color: var(--error-text);
    }

    &.Input_fieldset___error:focus-within {
      box-shadow: inset 0 0 0 2px var(--error-text);
      color: var(--error-text);
    }

    .Input {
      height: 32px;
      background: inherit;

      &::placeholder {
        color: var(--center-channel-color);
        opacity: 0.64;
      }
    }
  }

  .PaymentForm-saved {
    width: 442px;
    height: fit-content;
    box-sizing: border-box;
    padding: 24px;
    border: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
    margin-bottom: 16px;
    background: #fff;
    border-radius: 4px;
  }

  .PaymentForm-saved-title {
    margin-bottom: 16px;
    color: var(--secondary-blue);
    font-family: Metropolis;
    font-size: 14px;
    font-weight: 600;
    letter-spacing: 1.5px;
    line-height: 18px;
    opacity: 0.4;
    text-transform: uppercase;
  }

  .PaymentForm-saved-card {
    padding-bottom: 16px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.12);
    color: var(--secondary-blue);
    font-family: Source Sans Pro;
    font-size: 16px;
