.PictureSelector {
  display: flex;
  width: 96px;
  flex-direction: column;
}

.PictureSelector__imageContainer {
  position: relative;
  height: 96px;
}

.PictureSelector__image {
  height: 100%;
  border: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
  background-position: 50% 50%;
  background-size: cover;
  border-radius: 100%;
}

.PictureSelector__selectButton {
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  width: 28px;
  height: 28px;
  justify-content: center;
  padding: 4px;
  border: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
  background: var(--center-channel-bg);
  border-radius: 100%;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.12);

  i {
    align-self: center;
    color: rgba(var(--center-channel-color-rgb), 0.56);
    font-size: 16px;
    line-height: 18px;

    &::before {
      margin: 0;
    }
  }

  &:hover {
    border: 1px solid rgba(var(--center-channel-color-rgb), 0.16);
    box-shadow: 0 6px 14px rgba(0, 0, 0, 0.12);

    i {
      color: rgba(var(--center-channel-color-rgb), 0.72);
    }
  }

  &:active {
    border: 1px solid rgba(var(--button-bg-rgb), 0.16);
    background: linear-gradient(
        0deg,
        rgba(var(--button-bg-rgb), 0.08),
        rgba(var(--button-bg-rgb), 0.08)
      ),
      var(--center-channel-bg);

    i {
      color: var(--button-bg);
    }
  }

  &:focus {
    border: 2px solid var(--button-bg);
  }
}

.PictureSelector__removeButton {
  padding: 0;
  border: none;
  margin-top: 12px;
  background-color: transparent;
  color: var(--button-bg);
  font-size: 12px;
  font-weight: 600;

  &:focus {
    text-decoration-line: underline;
  }
}

