@import "utils/variables";

.modal-marketplace {
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;
  align-items: center;
  padding-top: 50px;
  color: var(--center-channel-color);
  font-size: 16px;

  @media (max-width: 768px) {
    padding-right: 15px;
    padding-left: 15px;
  }

  div.navigation-row {
    margin-top: 10px;

    div {
      font-size: 14px;
      text-align: center;

      button {
        font-size: 14px;
        font-weight: bold;
      }
    }

    div.count {
      padding-top: 8px;
    }
  }

  .input-clear {
    top: 32px;
    right: 36px;
    display: flex;
    width: 32px;
    height: 32px;
    align-items: center;
    justify-content: center;
    font-size: 16px;
  }

  .nav-tabs {
    font-size: 14px;

    > li {
      > a {
        padding: 10px 16px;
        background: transparent;
        transition: all 0.15s ease;

        &:hover,
        &:active,
        &:focus {
          background: transparent;
          color: var(--center-channel-color);
        }
      }

      &.active > a {
        color: var(--center-channel-color);
      }
    }
  }

  h1 {
    margin: 8px 0 24px;
    font-size: 28px;
  }

  h2 {
    font-weight: 300;

    strong {
      font-weight: 400;
    }
  }

  .more-modal__list {
    .more-modal__row {
      align-items: normal;

      .marketplace__tag {
        margin-left: 6px;
      }

      .update {
        padding: 10px 10px 0 0;
        border-top: 1px solid rgba(black, 0.1);
        margin: 10px 10px 0 0;
        font-size: 0.9em;
      }
    }

    .more-modal__description {
      margin: 2px 0 0;
      font-size: 0.9em;
    }

    padding-bottom: 80px;
  }

  .search_input {
    width: 720px;
    height: 40px;
    flex: 1;
    margin-top: 28px;
    margin-right: 16px;
    margin-bottom: 20px;
    margin-left: 16px;
    box-shadow: none;
  }

  .btn {
    min-width: 8rem;
    padding: 8px 16px;
    border-radius: 4px;
    font-size: 13px;
    font-weight: 600;
  }

  .btn-outline {
    border: 1px solid var(--button-bg);
    background: transparent;
  }

  .tabs {
    display: flex;
    width: 720px;
    flex-direction: column;
  }

  .subtitle {
    margin-left: 5px;
  }

  .plugin__list {
    display: flex;
    flex-direction: column;

    .plugin__row {
      display: flex;
      align-items: center;
      padding: 10px 15px;
      border-bottom: 1px solid $light-gray;

      &.clickable {
        cursor: pointer;
      }

      &:hover {
        .plugin__actions--round {
          opacity: 0.5;
        }
      }
    }
  }

  .icon__plugin {
    display: flex;
    height: 42px;
    flex: 0 0 42px;
    align-items: center;
    justify-content: center;
    margin-right: 4px;
    border-radius: 50%;

    svg {
      width: 32px;
      height: 32px;
      fill: var(--button-bg);
    }
  }

  .icon__plugin--background {
    padding: 6px;
    background-color: $white;

    svg {
      width: 24px;
      height: 24px;
    }
  }

  .no_plugins_div {
    text-align: center;
  }

  .item_error {
    background-color: rgba(var(--error-text-rgb), 0.08);
  }

  .error_text {
    color: var(--error-text);
    opacity: 1;
  }
}

.error-bar {
  position: fixed;
  z-index: 8;
  top: 0;
  overflow: hidden;
  width: 100%;
  min-height: $announcement-bar-height;
  max-height: $announcement-bar-height;
  padding: 5px 30px;
  background-color: var(--center-channel-bg);
  color: var(--error-text);

  .error-bar__content {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
    width: 100%;
    height: 100%;
    align-items: center;
    justify-content: center;
    background-color: rgba(var(--error-text-rgb), 0.12);
  }
}

