  display: flex;
  width: 420px;
  height: 100vh;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: auto;
  text-align: center;

  &--non-fullscreen {
    position: fixed;
    left: calc(50vw - 210px);
  }
}

.LaunchingWorkspace {
