@import "utils/variables";

.modal-backdrop.downgrade-modal-backdrop {
  z-index: 1050;
}

.DowngradeTeamRemovalModal {
  z-index: 1051;

  .modal {
    overflow: visible;
  }

  .modal-dialog {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 600px;
    height: 404px;
    border: none;
    margin: auto;
    transform: translate(-50%, -50%) !important;

    .modal-content {
      border: none;
      background-color: var(--center-channel-bg);
      border-radius: 12px;
      box-shadow: $elevation-6;
    }
  }

  .modal-header {
    height: 77px;
    min-height: 77px;
    border: none;
    border-radius: 12px;
  }

  .DowngradeTeamRemovalModal__header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 22px 32px;
    border: none;
    font-size: 22px;
    font-weight: 600;

    &::before,
    &::after {
      display: none;
    }

    // beat regular modal-header in specificity
    &.modal-header {
      background-color: var(--center-channel-bg);
      color: var(--center-channel-color);
    }

    .icon-close {
      padding: 0;
      border: none;
      -moz-appearance: none;
      -webkit-appearance: none;
      appearance: none;
      background: none;
      color: rgb(var(--center-channel-color-rgb), 0.56);
      cursor: pointer;
      font: inherit;
      outline: inherit;

      &:hover,
      &:active,
      &:focus {
        color: rgb(var(--center-channel-color-rgb), 0.56);
      }
    }
  }

  .modal-body {
    padding: 26px 32px;
    padding-top: 0;
    border: none;
    overflow-y: visible;
  }

  .DowngradeTeamRemovalModal__body {
