.SelfHostedPurchaseModal {
  overflow: hidden;
  height: 100%;

  .form-view {
    display: flex;
    overflow: hidden;
    width: 100%;
    height: 100%;
    flex-direction: row;
    flex-grow: 1;
    flex-wrap: wrap;
    align-content: top;
    justify-content: center;
    padding: 77px 107px;
    color: var(--center-channel-color);
    font-family: "Open Sans";
    font-size: 16px;
    font-weight: 600;

    .title {
      font-size: 22px;
      font-weight: 600;
    }

    .form {
      padding: 0 96px;
      margin: 0 auto;

      .form-row {
        display: flex;
        width: 100%;
        margin-bottom: 24px;
      }

      .form-row-third-1 {
        width: 66%;
        max-width: 288px;
        margin-right: 16px;

        .DropdownInput {
          z-index: 99999;
          margin-top: 0;
        }
      }

      .DropdownInput {
        position: relative;
        z-index: 999999;
        height: 36px;
        margin-bottom: 24px;

        .Input_fieldset {
          height: 43px;
        }
      }

      .form-row-third-2 {
        width: 34%;
        max-width: 144px;
      }

      .section-title {
        margin-bottom: 24px;
        color: rgba(var(--center-channel-color-rgb), 0.72);
        font-size: 16px;
        font-weight: 600;
        text-align: left;
      }

      .Input_fieldset {
        height: 40px;
        padding: 2px 1px;
        background: var(--center-channel-bg);

        .Input {
          height: 32px;
          background: inherit;
        }

        .Input_wrapper {
          margin: 0;
        }
      }
    }

    &--hide {
      display: none;
    }

    > .lhs {
      width: 25%;
    }

    > .center {
      width: 50%;
    }

    > .rhs {
      position: sticky;
      display: flex;
      width: 25%;
      flex-direction: column;
      align-items: center;

      .price-text {
        display: flex;
        align-items: baseline;
        padding: 8px 0;
        font-size: 32px;
        font-weight: 600;
        line-height: 1;

        .price-decimals {
          align-self: end;
          padding-bottom: 2px;
          font-size: 16px;

          &::before {
            content: ".";
          }
        }
      }

      .monthly-text {
