      padding-right: 170px;
      color: rgba(var(--center-channel-color-rgb), 0.72);
      font-size: 18px;
      font-weight: 400;
      line-height: 28px;
    }

    .signup-body-message {
      display: flex;
      width: 540px;
      flex-flow: column;
      align-self: flex-start;

      .signup-body-message-title {
        padding-right: 60px;
        color: var(--title-color-indigo-500);
        font-family: Metropolis;
        font-size: 80px;
        font-weight: 600;
        letter-spacing: -0.01em;
        line-height: 88px;
      }

      .signup-body-message-svg {
        position: relative;
        align-self: flex-end;

        svg {
          position: absolute;
          z-index: 2;
          bottom: -220px;
          left: -267px;
          display: block;
        }
      }

      &.custom-branding {
        padding: 0;

        &.with-brand-image {
          align-self: center;

          .signup-body-message-subtitle,
          .signup-body-custom-branding-markdown {
            text-align: center;
          }
        }

        .signup-body-message-subtitle {
          padding-right: 0;
        }

        .signup-body-custom-branding-image {
          max-height: 540px;
          align-self: center;
          margin-bottom: 28px;
          border-radius: 8px;
        }
      }

