      width: 540px;
      box-sizing: border-box;
      border: 1px solid rgba(var(--center-channel-color-rgb), 0.08);
      margin-left: 60px;
      background-color: var(--center-channel-bg);
      border-radius: 8px;
      box-shadow: var(--elevation-5);

      &.with-error {
        @include shake-horizontally;
      }

      .signup-body-card-content {
        display: flex;
        flex: 1;
        flex-flow: column;
        padding: 48px 56px;
        border: none;
        box-shadow: none;

        .signup-body-card-title {
          color: var(--center-channel-color);
          font-family: "Metropolis";
          font-size: 22px;
          font-style: normal;
          font-weight: 600;
          line-height: 28px;
        }

        .signup-body-custom-branding-markdown,
        .signup-body-message-subtitle {
          display: none;
        }

        .signup-body-card-banner {
          margin: 20px 0 10px;
        }

        .signup-body-card-form {
          .signup-body-card-form-email-input,
          .signup-body-card-form-name-input,
          .signup-body-card-form-password-input {
            margin-top: 22px;
          }

          .signup-body-card-form-button-submit {
            @include primary-button;
            @include button-large;

            width: 100%;
            margin-top: 30px;
          }
        }

        .signup-body-card-form-divider {
          display: flex;
          height: 1px;
          justify-content: center;
          margin: 40px 0;
          background: rgba(var(--center-channel-color-rgb), 0.08);
          text-align: center;

          .signup-body-card-form-divider-label {
            padding: 0 10px;
            background-color: var(--center-channel-bg);
            color: rgba(var(--center-channel-color-rgb), 0.56);
            font-size: 12px;
            line-height: 0;
          }
        }

        .signup-body-card-form-login-options {
          display: flex;
          flex: 1;
          flex-wrap: wrap;
          justify-content: space-between;
          column-gap: 8px;
          row-gap: 8px;

          &.column {
            flex-direction: column;
            margin-top: 22px;
            row-gap: 24px;
          }
        }

        .signup-body-card-agreement {
          margin-top: 32px;
          color: rgba(var(--center-channel-color-rgb), 0.56);
          font-size: 11px;
          line-height: 16px;

          a {
            @include link;

            font-size: 11px;
          }
        }
      }
    }

    .signup-body-custom-branding-markdown {
      flex: 1;

      ul + p,
      ol + p {
        margin-top: 0.6em;
      }

      p + ul,
      p + ol {
        margin-top: 0.6em;
      }

      img {
        max-width: 450px;
      }

      p {
        width: 100%;
        margin: 0;
        color: rgba(var(--center-channel-color-rgb), 0.72);
        font-size: 18px;
        font-weight: 400;
        line-height: 28px;
        white-space: pre-wrap;
        word-break: break-word;
      }
    }

    .signup-body-content-button-container {
      display: flex;
      flex: 1;
      justify-content: center;

      .signup-body-content-button-return {
        @include primary-button;
        @include button-medium;
      }
    }
  }
}

