    );
    @include leveled-color(
      $hover: var(--center-channel-color-rgb),
      $active: var(--button-bg-rgb)
    );
  }

  &.Button___large {
    font-size: 14px;

    &.Button___icon {
      width: 32px;
      height: 32px;
      padding: 7px 0;
      font-size: 18px;
      line-height: 18px;
    }
  }

  .Button_label {
    display: inline-block;
    max-width: 100%;

    &.margin_top {
      margin-top: 4px;
    }
  }

  .Button_prepended {
    margin-right: 0.5em;
