@charset 'UTF-8';

.user-groups-modal-create,
.user-groups-modal-update,
.view-user-groups-modal,
.user-groups-modal {
  &.modal-dialog {
    margin-top: 50px;
  }

  .modal-content {
    border-radius: 8px;

    .ad_ldap_upsell_banner {
      position: sticky;
      z-index: 4;
      right: 12px;
      bottom: 6px;
      left: 12px;
      display: flex;
      height: 44px;
      align-items: center;
      justify-content: space-between;
      padding: 12px 8px;
      margin: 0 12px;
      background-color: var(--denim-sidebar-active-border);
      border-radius: 8px;
      box-shadow: 0 4px 6px rgba(0, 0, 0, 0.12);
      color: var(--button-color);

      .message {
        display: flex;
        gap: 8px;
      }

      .btn-container {
        display: flex;
        gap: 13px;
      }

      .banner-close {
        height: 4rem;
        border: none;
        margin-top: 0;
        background: none;
        font-size: 25px;
        font-weight: 400;
        line-height: 18px;
      }

      .ad-ldap-banner-btn {
        border: none;
        background: none;
        color: var(--button-color);
        font-size: 14px;
        font-weight: 600;
        line-height: 14px;
      }
    }

    .ad_ldap_upsell_confirm {
      position: sticky;
      z-index: 4;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      overflow: hidden;
      width: 100%;
      height: 100%;
      border-radius: 0 0 8px 8px;

      .upsell-confirm-backdrop {
        position: fixed;
        z-index: 5;
        bottom: 164px;
        width: 100%;
        height: calc(100% - 164px);
        background: rgba(0, 0, 0, 0.16);
      }

      .upsell-confirm-foreground {
        height: 148px;
        padding: 16px 24px;
        background: var(--button-color);
      }

      .title {
        color: #000;
        font-size: 14px;
        font-weight: 600;
        line-height: 20px;
      }

      .subtitle {
        color: var(--center-channel-color);
        font-size: 14px;
        font-weight: 400;
        line-height: 20px;
      }

      .btns-container {
        display: flex;
        justify-content: right;
        gap: 10px;

        .confrim-btn {
          width: 100px;
          height: 32px;
          padding: 10px 16px;
          border: none;
          background: rgba(28, 88, 217, 0.08);
          border-radius: 4px;
          color: var(--denim-button-bg);
          font-size: 12px;
          font-weight: 600;
          line-height: 10px;
        }

        .learn-more {
          background: rgba(var(--denim-button-bg-rgb), 0.08);
          color: var(--denim-button-bg);
        }

        .continue {
          background: var(--denim-button-bg);
          color: var(--button-color);
        }
      }
    }

    .modal-header {
      h1#createGroupsModalTitleWithBack,
      h1#updateGroupsModalTitle,
      h1#addUsersToGroupsModalLabel {
        margin-left: 44px;
      }

      button.user-groups-create {
        box-sizing: border-box;
        border: 1px solid var(--button-bg);
        margin: auto;
        margin-right: 46px;
        background-color: var(--center-channel-bg);
        border-radius: 4px;
        color: var(--button-bg);

        &:hover {
          background-color: rgba(var(--button-bg-rgb), 0.08);
        }

        &:active {
          background-color: rgba(var(--button-bg-rgb), 0.16);
        }

        &:focus {
          box-shadow: inset 0 0 0 2px var(--sidebar-text-active-border);
          outline: none;
        }
      }
    }
  }

  .modal-body {
    overflow: hidden;
    max-height: 100%;
    padding: 0;

    .no-results__wrapper {
      padding-bottom: 60px;
    }

    .user-groups-search {
      position: relative;
      margin: 6px 24px 8px;

      .Input_container {
        .Input_wrapper {
          margin: 6px 0;
          line-height: 38px;
        }

        .Input {
          font-size: 14px;
          line-height: 38px;
        }
      }

      .fa-search {
        position: absolute;
        top: 15px;
        left: 16px;
        color: rgba(var(--center-channel-color-rgb), 0.64);
        font-size: 16px;
        pointer-events: none;
      }
    }

    .more-modal__dropdown {
      width: 100%;
      padding: 0 24px 24px;
      border-bottom: 1px solid rgba(var(--center-channel-color-rgb), 0.08);

      #groupsFilterDropdown {
        padding: 6px;
        border-radius: 4px;
        line-height: 12px;

        &:hover {
          background: rgba(var(--center-channel-color-rgb), 0.08);
        }

        &:active {
          background: rgba(var(--button-bg-rgb), 0.08);
        }

        a {
          line-height: 12px;

          span {
            color: var(--button-bg);
            font-size: 12px;
            line-height: 12px;
          }
        }
      }

      .groups-filter-btn span {
        width: 100%;

        i {
          display: flex;
          align-items: center;
          margin-left: auto;
          color: var(--button-bg);
        }
      }
    }

    .user-groups-modal__content {
      overflow: auto;

      &.user-groups-list {
        position: relative;
        max-height: 450px;
        padding-top: 24px;
        padding-bottom: 16px;
        overflow-y: scroll; // for Firefox and browsers that doesn't support overflow-y:overlay property
        overflow-y: overlay;

        &::-webkit-scrollbar {
          width: 20px;
        }

        &::-webkit-scrollbar-track {
          background: rgba(0, 0, 0, 0);
        }

        &::-webkit-scrollbar-thumb {
          border: 8px rgba(0, 0, 0, 0) solid;
          background: rgba(var(--center-channel-color-rgb), 0.24);
          background-clip: padding-box;
          border-radius: 9999px;
        }

        .group-row {
          display: flex;
          align-items: center;
          padding: 14px 32px;

          &:hover {
            background: rgba(var(--center-channel-color-rgb), 0.08);
            cursor: pointer;
          }

          &:active {
            background: rgba(var(--button-bg-rgb), 0.08);
          }

          .group-display-name,
          .group-name {
            display: block;
            overflow: hidden;
            max-width: 200px;
            text-overflow: ellipsis;
            white-space: nowrap;
          }

          .group-name {
            padding-left: 8px;
            color: rgba(var(--center-channel-color-rgb), 0.64);
          }

          .group-member-count {
            margin-right: 8px;
            margin-left: auto;
            color: rgba(var(--center-channel-color-rgb), 0.64);
            font-size: 12px;
            line-height: 16px;
          }

          .group-action {
            width: 24px;

            .MenuWrapper {
              width: 24px;
              margin-left: auto;
            }

            .group-actions-menu {
              span.MenuItem__primary-text {
                font-size: 14px;
                line-height: 20px;

                &.leave-group-action {
                  color: var(--dnd-indicator);
                }

                span.icon {
                  display: inline-flex;
                  width: auto;
                  height: auto;
                  align-self: center;
                  padding-right: 12px;
                  color: rgba(var(--center-channel-color-rgb), 0.56);
                  font-size: 18px;
                  line-height: 18px;
                }
              }
            }
          }

          .group-action button.action-wrapper {
            display: flex;
            width: 24px;
            height: 24px;
            flex-direction: column;
            align-items: center;
            padding: 4px 6px;
            border: none;
            margin-left: auto;
            background: transparent;
            border-radius: 4px;
            color: rgba(var(--center-channel-color-rgb), 0.56);
