.DropdownInput.hybrid_container {
  z-index: 999999;
  margin-top: 20px;

  .Input_wrapper {
    padding: 0;
    margin: 0;

    &.input_hybrid_wrapper {
      display: inline-block;
      flex-grow: 1;
      transition: max-width 0.1s ease-out;

      &.showInput {
        transition: max-width 0.1s ease-in;
      }
    }

    &.dropdown_hybrid_wrapper {
      display: inline-block;

      &.showInput {
        float: right;
        transition: width 0.1s ease-out;
      }
    }
  }

  .Input {
    font-size: 14px;
    line-height: 20px;

    &.Input___focus .a11y--focused {
      box-shadow: none;
    }
  }

  .Input_fieldset {
    position: relative;
    display: flex;
    padding: 0;
  }

  .Input_legend {
    position: absolute;
    z-index: 1;
    top: -8px;
    width: auto;
    padding: 0 5px;
    border-bottom: none;
    margin-bottom: 0;
    margin-left: 8px;
    font-size: 10px;
  }

  .DropDown__menu {
    z-index: 1000000;
    margin-top: 2px;
  }

  .DropdownInput__indicatorsContainer {
    margin-right: 8px;

    i {
      color: rgba(var(--center-channel-color-rgb), 0.64);
      font-weight: normal;

      &::before {
        margin: 0;
      }
    }
  }

  .DropdownInput__option > div {
    z-index: 999999;
    padding: 10px 24px;
    cursor: pointer;
    line-height: 16px;
  }

  .DropdownInput__option.selected > div {
    background-color: rgba(var(--button-bg-rgb), 0.08);
    color: var(--center-channel-color);
  }

  .DropdownInput__option.focused > div {
    background-color: rgba(var(--center-channel-color-rgb), 0.08);
  }

  .Input_fieldset:focus-within {
    border-width: 2px;
    box-shadow: none;
  }

  .Input.form-control {
    height: 40px;
    border: none;
    border-right: 1px solid rgba(var(--center-channel-color-rgb), 0.16);
    border-radius: 4px 0 0 4px;

    &::-webkit-outer-spin-button,
    &::-webkit-inner-spin-button {
      margin: 0;
      -webkit-appearance: none;
    }

    &[type="number"] {
      -moz-appearance: textfield;
    }

    &:focus {
      border-right: 1px solid rgba(var(--center-channel-color-rgb), 0.16);
    }
  }
}

