  &__pulsating-dot-ctr[data-pulsating-dot-placement="bottom"] {
    bottom: 0;
    left: calc(50% - 6px);
    transform: translate(0, -6px);
  }

  &__pulsating-dot-ctr[data-pulsating-dot-placement="bottom-start"] {
    bottom: 0;
    left: 0;
    transform: translate(6px, -6px);
